import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // 변경
import '../styles/MainPage.css';

function MainPage() {
    const [searchTerm, setSearchTerm] = useState('');
    const [recentSearches, setRecentSearches] = useState([]);
    const [autocompleteItems, setAutocompleteItems] = useState([]);

    const navigate = useNavigate(); // 변경

    useEffect(() => {
        const storedSearches = JSON.parse(localStorage.getItem('recentSearches') || '[]');
        setRecentSearches(storedSearches);
    }, []);

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        if (searchTerm && !recentSearches.includes(searchTerm)) {
            const newSearches = [...recentSearches, searchTerm];
            setRecentSearches(newSearches);
            localStorage.setItem('recentSearches', JSON.stringify(newSearches));
        }

        navigate(`/search-results?name=${searchTerm}`); // 변경
    };

    const handleInputChange = (e) => {
        const value = e.target.value;
        console.log(value);
        setSearchTerm(value);

        fetch(`/search/autocomplete?name=${value}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log(data)
                if (Array.isArray(data)) {
                    // 중복 항목 제거
                    const uniqueItems = [...new Set(data)];
                    setAutocompleteItems(uniqueItems);
                } else {
                    console.error("Expected an array but received:", data);
                    setAutocompleteItems([]);
                }
            }).catch(error => {
            console.error("Error fetching data:", error);
            setAutocompleteItems([]);
        });
    };

    return (
        <div className="main-page">
            <header className="header">
                <img src={logo} className="logo" alt="kkini logo" onClick={() => navigate('/')}/>
                <div className="search-bar">
                    <form onSubmit={handleSearchSubmit}>
                        <input
                            type="text"
                            name="name"
                            placeholder="Enter product name"
                            value={searchTerm}
                            onChange={handleInputChange}
                            list="recentSearches"
                        />
                        <div className="autocomplete-items">
                            {Array.isArray(autocompleteItems) && autocompleteItems.map(productName => (
                                <div key={productName} onClick={() => setSearchTerm(productName)}>
                                    {productName}
                                </div>
                            ))}
                        </div>
                        <input type="submit" value="Search" />
                    </form>
                </div>
                <div className="nav-icons">
                    <span className="icon">👤</span> {/* 마이페이지 아이콘 */}
                    <span className="icon"> ♥ </span> {/* 찜하기 아이콘 */}
                </div>
                <nav className="nav-links">
                    <a href="#">로그인</a> |
                    <a href="#">소개</a> |
                    <a href="#">도움말</a>
                </nav>
            </header>

            <div className="menu-bar">
                <button>끼니 PICK</button>
                <button>끼니 랭킹</button>
                <button>끼니 그린 랭킹</button>
                <button>커뮤니티</button>
            </div>

            <section className="ranking-section">
                <h2>끼니 PICK</h2>
                <p>끼니의 친구가 되어, 나를 위한 상품들을 만나보세요!</p>
                <div className="ranking-images">
                    {/* ... (랭킹 이미지 코드) */}
                </div>
            </section>

            <section className="ranking-section">
                <h2>끼니 랭킹</h2>
                <p>현재 인기만점 제품</p>
                <div className="ranking-images">
                    {/* ... (랭킹 이미지 코드) */}
                </div>
            </section>

            <section className="ranking-section">
                <h2>끼니 그린 랭킹</h2>
                <p>간편하고, 건강하게!</p>
                <div className="ranking-images">
                    {/* ... (랭킹 이미지 코드) */}
                </div>
            </section>
            <footer className="footer">
                <div className="footer-left">
                    <p>고객센터 : 010-3556-6717</p>
                    <p>이메일 : dlgudtmd1022naver.com</p>
                    <p>평일: 09:00 - 17:00 (점심시간 12:00 - 13:00)</p>
                    <p>주말/공휴일 휴무</p>
                </div>
                <div className="footer-right">
                    <p>법인명(상호) : KKINI Corp.</p>
                    <p>대표자 : 이 형 승 </p>
                    <p>주소 : Street, Seoul, Korea</p>
                    <p>팩스 : 02-1234-5678</p>
                </div>
                <div className="footer-bottom">
                    <p>© 2023 KKINI. All rights reserved.</p>
                    <div className="footer-links">
                        <a href="/privacy">개인정보처리</a> | <a href="/terms">이용약관</a>
                    </div>
                </div>
            </footer>
        </div>
    );
}

export default MainPage;
