import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/SearchResultPage.css';
import Header from "../components/Header";
import Filters from "../components/Filters";
import ProductList from "../components/ProductList";
import Footer from "../components/Footer";

const SERVER_URL = "http://localhost:8080";

function SearchResultPage() {
    const [searchTerm, setSearchTerm] = useState('');
    const [autocompleteItems, setAutocompleteItems] = useState([]);
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const searchTermFromParams = searchParams.get('searchTerm') || '';
    const navigate = useNavigate();
    const [items, setItems] = useState([]);
    const [displayedItems, setDisplayItems] = useState([]);

    useEffect(() => {
        let endpoint = `${SERVER_URL}/search/products?searchTerm=${searchTermFromParams}`;

        fetch(endpoint)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                if (data.message) {
                    console.warn(data.message);
                    setItems([]);
                    setDisplayItems([]);
                } else if (Array.isArray(data)) {
                    const transformedData = data.map(item => ({
                        ...item,
                        isKkini: !!item.isKkini
                    }));
                    setItems(transformedData);
                    setDisplayItems(transformedData);
                } else {
                    console.error("Unexpected response format:", data);
                    setItems([]);
                    setDisplayItems([]);
                }
            })
            .catch(error => {
                console.error("Error fetching products:", error);
            });
    }, [searchTermFromParams]);

    return (
        <div className="search-result-page">
            <Header
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                autocompleteItems={autocompleteItems}
                setAutocompleteItems={setAutocompleteItems}
            />
            <div className="content-wrapper">
                <Filters
                    items={items}
                    setDisplayItems={setDisplayItems}
                    searchName={searchTermFromParams}
                    setItems={setItems}
                />
                <ProductList items={displayedItems} />
            </div>
            <Footer />
        </div>
    );
}
export default SearchResultPage;
