import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/SearchResultPage.css';
import Header from "../components/Header";
import Filters from "../components/Filters";
import ProductList from "../components/ProductList";
import Footer from "../components/Footer";

function SearchResultPage() {
    const [items, setItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [autocompleteItems, setAutocompleteItems] = useState([]);
    const location = useLocation();
    const navigate = useNavigate();
    const searchParams = new URLSearchParams(location.search);
    const [searchName, setSearchName] = useState(searchParams.get('name') || '');

    useEffect(() => {
        const storedSearches = JSON.parse(localStorage.getItem('recentSearches') || '[]');
        setSearchTerm(storedSearches);
    }, []);

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        navigate(`/search-results?name=${searchTerm}`);
    };

    const handleInputChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        fetch(`/search/autocomplete?name=${value}`)
            .then(response => response.json())
            .then(data => {
                if (Array.isArray(data)) {
                    const uniqueItems = [...new Set(data)];
                    setAutocompleteItems(uniqueItems);
                } else {
                    setAutocompleteItems([]);
                }
            });
    };

    useEffect(() => {
        fetch(`/search/products?name=${searchName}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setItems(data))
            .catch(error => console.error("Error fetching data:", error));
    }, [searchName]);

    return (
        <div className="search-result-page">
            <header className="header">
                <div className="logo" onClick={() => navigate('/')}>KKINI</div>
                <div className="search-bar">
                    <form onSubmit={handleSearchSubmit}>
                        <input
                            type="text"
                            name="name"
                            placeholder="Enter product name"
                            value={searchTerm}
                            onChange={handleInputChange}
                            list="recentSearches"
                        />
                        <div className="autocomplete-items">
                            {Array.isArray(autocompleteItems) && autocompleteItems.map(productName => (
                                <div key={productName} onClick={() => setSearchTerm(productName)}>
                                    {productName}
                                </div>
                            ))}
                        </div>
                        <input type="submit" value="Search" />
                    </form>
                </div>
                <div className="nav-icons">
                    <span className="icon">👤</span> {/* 마이페이지 아이콘 */}
                    <span className="icon"> ♥ </span> {/* 찜하기 아이콘 */}
                </div>
                <nav className="nav-links">
                    <a href="#">로그인</a> |
                    <a href="#">소개</a> |
                    <a href="#">도움말</a>
                </nav>
            </header>

            <div className="content-wrapper">
                <aside className="filters">
                    <h3>끼니 선택</h3>
                    <ul>
                        <li><input type="checkbox" id="kkini" name="kkini" /><label htmlFor="kkini">끼니</label></li>
                        <li><input type="checkbox" id="kkini-green" name="kkini-green" />
                            <label htmlFor="kkini-green">끼니 그린</label></li>
                    </ul>
                    <h3>필터</h3>
                    {/* 필터 관련 내용 */}
                    <h3>카테고리</h3>
                    {/* 카테고리 관련 내용 */}
                </aside>

                <main className="product-list">
                    {items.map(item => (
                        <div key={item.itemId} className="product-item">
                            <img src={item.productImage} alt={item.name} width="100" />
                            <h4>{item.name}</h4>
                            <p className="rating-display">
                                <span className="fa fa-star"></span>
                                {item.averageRating.toFixed(2)} ({item.reviewCount} reviews)
                            </p>
                        </div>
                    ))}
                    <button className="load-more">더보기</button>
                </main>
            </div>

            <footer className="footer">
                <div className="footer-left">
                    <p>고객센터 : 010-3556-6717</p>
                    <p>이메일 : dlgudtmd1022naver.com</p>
                    <p>평일: 09:00 - 17:00 (점심시간 12:00 - 13:00)</p>
                    <p>주말/공휴일 휴무</p>
                </div>
                <div className="footer-right">
                    <p>법인명(상호) : KKINI Corp.</p>
                    <p>대표자 : 이 형 승 </p>
                    <p>주소 : Street, Seoul, Korea</p>
                    <p>팩스 : 02-1234-5678</p>
                </div>
                <div className="footer-bottom">
                    <p>© 2023 KKINI. All rights reserved.</p>
                    <div className="footer-links">
                        <a href="/privacy">개인정보처리</a> | <a href="/terms">이용약관</a>
                    </div>
                </div>
            </footer>
        </div>
    )};
export default SearchResultPage;
