import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/SearchResultPage.css';
import Header from "../components/Header";
import Filters from "../components/Filters";
import ProductList from "../components/ProductList";
import Footer from "../components/Footer";

function SearchResultPage() {
    const [items, setItems] = useState([]);
    const [displayedItems, setDisplayItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [autocompleteItems, setAutocompleteItems] = useState([]);
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const searchName = searchParams.get('name') || '';
    const navigate = useNavigate();
    const [selectedCategories, setSelectedCategories] = useState([]);
    const [selectedFilters, setSelectedFilters] = useState([]);

    const filteredItems = items.filter(item =>
        (selectedCategories.length === 0 || selectedCategories.includes(item.category)) &&
        selectedFilters.every(filter => item.filters && item.filters.includes(filter))
    );

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        navigate(`/search-results?name=${searchTerm}`);
    };

    const handleInputChange = (e) => {

        const value = e.target.value;
        setSearchTerm(value);

        // Autocomplete logic can be added here if needed
    };

    return (
        <div className="search-result-page">
            <Header
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                autocompleteItems={autocompleteItems}
                setAutocompleteItems={setAutocompleteItems}
                handleSearchSubmit={handleSearchSubmit}
                handleInputChange={handleInputChange}
            />
            <div className="content-wrapper">
                <Filters
                    items={items}
                    searchName={searchName}
                    setItems={setItems}
                    setDisplayItems={setDisplayItems}
                    onCategoryChange={(category, isChecked) => {
                        if (isChecked) {
                            setSelectedCategories(prev => [...prev, category]);
                        } else {
                            setSelectedCategories(prev => prev.filter(c => c !== category));
                        }
                    }}
                    onFilterChange={(filter, isChecked) => {
                        if (isChecked) {
                            setSelectedFilters(prev => [...prev, filter]);
                        } else {
                            setSelectedFilters(prev => prev.filter(f => f !== filter));
                        }
                    }}
                    selectedCategories={selectedCategories}
                    selectedFilters={selectedFilters}
                />
                <ProductList items={filteredItems} />
            </div>
            <Footer />
        </div>
    );
}

export default SearchResultPage;
