import React from 'react';

function Filters({ onCategoryChange }) {
    const categories = ["간식", "육가공", "음료", "즉석섭취식품"];

    return (
        <div className="filters">
            <h3>카테고리</h3>
            {categories.map(category => (
                <label key={category}>
                    <input
                        type="checkbox"
                        value={category}
                        onChange={(e) => onCategoryChange(e.target.value, e.target.checked)}
                    />
                    {category}
                </label>
            ))}
        </div>
    );
}
