import React from 'react';

function Filters({ searchName, setItems, setDisplayItems, onCategoryChange, onFilterChange }) {
    const categories = ["간식", "육가공", "음료", "즉석섭취식품"];
    const filters = ["저칼로리", "슈가프리", "로우슈가", "저탄수화물",
        "키토", "트랜스지방", "고단백", "저나트륨", "포화지방", "저지방"];
    const kkinis = ["끼니", "끼니 그린"];

    const handleKkiniChange = (e) => {
        const kkiniType = e.target.value;
        let endpoint = `/search/products?name=${searchName}`;

        if (kkiniType !== 'all') {
            endpoint = `/search/kkini-products/${kkiniType}`;
        }

        fetch(endpoint)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                if (Array.isArray(data)) {
                    setItems(data);
                    setDisplayItems(data.slice(0, 10));
                } else {
                    console.error("Expected an array but received:", data);
                    setItems([]);
                }
            })
            .catch(error => {
                console.error("Error fetching products:", error);
            });
    };

    return (
        <div className="filters">
            <h3>끼니 선택</h3>
            <div className="kkini-selection">
                <label>
                    <input type="radio" name="kkini" value="all" onChange={handleKkiniChange} defaultChecked />
                    전체 (끼니 & 끼니 그린)
                </label>
                <label>
                    <input type="radio" name="kkini" value="kkini" onChange={handleKkiniChange} />
                    끼니
                </label>
                <label>
                    <input type="radio" name="kkini" value="kkiniGreen" onChange={handleKkiniChange} />
                    끼니 그린
                </label>
            </div>

            <h3>카테고리</h3>
            {categories.map(category => (
                <div key={category} className="filter-item">
                    <label>
                        <input
                            type="checkbox"
                            value={category}
                            onChange={(e) => onCategoryChange(e.target.value, e.target.checked)}
                        />
                        {category}
                    </label>
                </div>
            ))}
            <h3>필터</h3>
            {filters.map(filter => (
                <div key={filter} className="filter-item">
                    <label>
                        <input
                            type="checkbox"
                            value={filter}
                            onChange={(e) => onFilterChange(e.target.value, e.target.checked)}
                        />
                        {filter}
                    </label>
                </div>
            ))}
        </div>
    );
}

export default Filters;
