import React from 'react';

function Filters({ items, setDisplayItems, onCategoryChange, onFilterChange,
                     selectedCategories, selectedFilters }) {
    const categories = ["간식", "육가공", "음료", "즉석섭취식품"];
    const filters = ["저칼로리", "슈가프리", "로우슈가", "저탄수화물", "키토", "트랜스지방",
        "고단백", "저나트륨", "포화지방", "저지방"];

    const handleKkiniChange = (e) => {
        const kkiniType = e.target.value;

        if (kkiniType === 'kkini') {
            setDisplayItems(items.filter(item => item.isKkini));
        } else if (kkiniType === 'kkiniGreen') {
            setDisplayItems(items.filter(item => !item.isKkini));
        } else {
            setDisplayItems(items);  // 전체 상품을 displayedItems에 설정
        }
    };

    const handleCategoryChange = (category, isChecked) => {
        onCategoryChange(category, isChecked);
        if (isChecked) {
            // 카테고리가 선택되면 해당 카테고리로 검색
            window.location.href = `${SERVER_URL}/search-results?categoryName=${category}`;
        }
    };

    return (
        <div className="filters">
            <h3>끼니 선택</h3>
            <div className="kkini-selection">
                <label>
                    <input type="radio" name="kkini" value="kkini" onChange={handleKkiniChange} defaultChecked/>
                    끼니
                </label>
                <label>
                    <input type="radio" name="kkini" value="kkiniGreen" onChange={handleKkiniChange} />
                    끼니 그린
                </label>
            </div>

            <h3>카테고리</h3>
            {categories.map(category => (
                <div key={category} className="category-selection">
                    <label>
                        <input
                            type="checkbox"
                            value={category}
                            onChange={(e) => handleCategoryChange(e.target.value, e.target.checked)}
                            checked={selectedCategories.includes(category)}
                        />
                        {category}
                    </label>
                </div>
            ))}

            <h3>필터</h3>
            {filters.map(filter => (
                <div key={filter} className="filter-selection">
                    <label>
                        <input
                            type="checkbox"
                            value={filter}
                            onChange={(e) => onFilterChange(e.target.value, e.target.checked)}
                            checked={selectedFilters.includes(filter)}
                        />
                        {filter}
                    </label>
                </div>
            ))}
        </div>
    );
}

export default Filters;
