import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/SearchResultPage.css';
import Header from "../components/Header";
import Filters from "../components/Filters";
import ProductList from "../components/ProductList";
import KkiniChecked from "../components/KkiniChecked";
import Footer from "../components/Footer";

const SERVER_URL = "http://localhost:8080";

function SearchResultPage(searchResults) {
    const [searchTerm, setSearchTerm] = useState('');
    const [autocompleteItems, setAutocompleteItems] = useState([]);
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const searchTermFromParams = searchParams.get('searchTerm') || '';
    const navigate = useNavigate();
    const [items, setItems] = useState([]);
    const [displayedItems, setDisplayItems] = useState([]);
    const [error, setError] = useState(null);
    const [filteredResults, setFilteredResults] = useState(searchResults);
    const [originalItems, setOriginalItems] = useState([]);

    useEffect(() => {
        setFilteredResults(searchResults);
    }, [searchResults]);

    useEffect(() => {
        const endpoint = `${SERVER_URL}/search/products?searchTerm=${searchTermFromParams}`;

        fetch(endpoint)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                if (data.message) {
                    setError(data.message);
                    setItems([]);
                    setOriginalItems([]);
                } else if (Array.isArray(data)) {
                    setItems(data);
                    setOriginalItems(data);
                } else {
                    const errorMsg = "Unexpected response format.";
                    setError(errorMsg);
                    setItems([]);
                    setOriginalItems([]); // 추가된 부분
                }
            })
            .catch(error => {
                setError(error.message || "Error fetching products.");
            });
    }, [searchTermFromParams]);

    const handleKkiniChecked = (isChecked) => {
        let endpoint;

        if (isChecked) {
            // 끼니 그린(isKkini=false) 상품만 보여줄 엔드포인트
            endpoint = `${SERVER_URL}/category/kkini?searchTerm=${searchTermFromParams}&isKkini=false`;
        } else {
            // 모든 상품을 보여줄 엔드포인트
            endpoint = `${SERVER_URL}/search/products?searchTerm=${searchTermFromParams}`;
        }
        fetch(endpoint)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                if (data.message) {
                    setError(data.message);
                    setItems([]);
                } else if (Array.isArray(data)) {
                    setItems(data);
                } else {
                    const errorMsg = "Unexpected response format.";
                    setError(errorMsg);
                    setItems([]);
                }
            })
            .catch(error => {
                setError(error.message || "Error fetching products.");
            });
    }

    const handleInputChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
    };

    return (
        <div className="search-result-page">
            <Header
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                autocompleteItems={autocompleteItems}
                setAutocompleteItems={setAutocompleteItems}
            />
            <div className="content-wrapper">
                {error ? (
                    <div className="error-message">
                        {error}
                    </div>
                ) : (
                    <>
                        <Filters
                            items={items}
                            setDisplayItems={setDisplayItems}
                            searchName={searchTermFromParams}
                            setItems={setItems}
                        />
                        <KkiniChecked onKkiniChecked={handleKkiniChecked}/>
                        <ProductList items={items} />
                    </>
                )}
            </div>
            <Footer />
        </div>
    );
}

export default SearchResultPage;
