import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/SearchResultPage.css';
import Header from "../components/Header";
import Filters from "../components/Filters";
import ProductList from "../components/ProductList";
import Footer from "../components/Footer";

function SearchResultPage() {
    const [items, setItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [autocompleteItems, setAutocompleteItems] = useState([]);
    const location = useLocation();
    const navigate = useNavigate();
    const searchParams = new URLSearchParams(location.search);
    const [searchName, setSearchName] = useState(searchParams.get('name') || '');

    useEffect(() => {
        const storedSearches = JSON.parse(localStorage.getItem('recentSearches') || '[]');
        setSearchTerm(storedSearches);
    }, []);

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        navigate(`/search-results?name=${searchTerm}`);
    };

    const handleInputChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        fetch(`/search/autocomplete?name=${value}`)
            .then(response => response.json())
            .then(data => {
                if (Array.isArray(data)) {
                    const uniqueItems = [...new Set(data)];
                    setAutocompleteItems(uniqueItems);
                } else {
                    setAutocompleteItems([]);
                }
            });
    };

    useEffect(() => {
        fetch(`/search/products?name=${searchName}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setItems(data))
            .catch(error => console.error("Error fetching data:", error));
    }, [searchName]);

    return (
        <div className="search-result-page">
            <Header
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                autocompleteItems={autocompleteItems}
                handleSearchSubmit={handleSearchSubmit}
                handleInputChange={handleInputChange}
            />
            <div className="content-wrapper">
                <Filters />
                <ProductList items={items} />
            </div>
            <Footer />
        </div>
    );
}
export default SearchResultPage;
