import React from 'react';

function Filters({ setItems, setDisplayItems }) {
    const categories = ["간식", "육가공", "음료", "즉석섭취식품"];
    const filters = ["저칼로리", "슈가프리", "로우슈가", "저탄수화물",
        "키토", "트랜스지방", "고단백", "저나트륨", "포화지방", "저지방"];
    const kkinis = ["끼니", "끼니 그린"];
    const SERVER_URL = "http://localhost:8080";

    const handleKkiniChange = async (kkini, isChecked) => {
        if (isChecked) {
            try {
                const response = await fetch
                (`${SERVER_URL}/search/kkini-products/${encodeURIComponent(kkini)}`);
                const data = await response.json();
                if (Array.isArray(data)) {
                    setItems(data);
                    setDisplayItems(data.slice(0, 5));
                } else {
                    console.error("Expected an array but received:", data);
                    setItems([]);
                }
            } catch (error) {
                console.error("Error fetching products by kkini type:", error);
            }
        }
    };

    const handleCategoryChange = async (category, isChecked) => {
        if (isChecked) {
            try {
                const response =
                    await fetch(`${SERVER_URL}/products/categoryChecked?categoryName=${category}`);
                const data = await response.json();
                if (Array.isArray(data)) {
                    setItems(data);
                    setDisplayItems(data.slice(0, 10));
                } else {
                    console.error("Expected an array but received:", data);
                    setItems([]);
                }
            } catch (error) {
                console.error("Error fetching products by category:", error);
            }
        }
    };

    const handleFilterChange = (filter, isChecked) => {
        // 필터 변경 로직
    };

    return (
        <main className="product-list">
            {displayedItems.map(item => (
                <div key={item.productId} className="product-item">
                    <img src={item.productImage} alt={item.name} width="100" />
                    <h4>{item.name}</h4>
                    <p className="rating-display">
                        <span>⭐</span>
                        {item.averageRating.toFixed(2)} ({item.reviewCount} reviews)
                    </p>
                </div>
            ))}
            {items && displayedItems.length < items.length && (
                <button onClick={handleLoadMore} className="load-more">더보기</button>
            )}
        </main>
    );
}

export default ProductList;
