import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // 변경
import '../styles/MainPage.css';
import logo from '../assets/images/kkini_logo.png'
import Header from "../components/Header";
import NavigationBar from "../components/NavigationBar";
import RankingSection from "../components/RankingSection";
import Footer from "../components/Footer";

function MainPage() {
    const [searchTerm, setSearchTerm] = useState('');
    const [recentSearches, setRecentSearches] = useState([]);
    const [autocompleteItems, setAutocompleteItems] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {
        const storedSearches = JSON.parse(localStorage.getItem('recentSearches') || '[]');
        setRecentSearches(storedSearches);
    }, []);

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        if (searchTerm && !recentSearches.includes(searchTerm)) {
            const newSearches = [...recentSearches, searchTerm];
            setRecentSearches(newSearches);
            localStorage.setItem('recentSearches', JSON.stringify(newSearches));
        }

        navigate(`/search-results?name=${searchTerm}`);
    };

    const handleInputChange = (e) => {
        const value = e.target.value;
        console.log(value);
        setSearchTerm(value);

        fetch(`/search/autocomplete?name=${value}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log(data)
                if (Array.isArray(data)) {
                    // 중복 항목 제거
                    const uniqueItems = [...new Set(data)];
                    setAutocompleteItems(uniqueItems);
                } else {
                    console.error("Expected an array but received:", data);
                    setAutocompleteItems([]);
                }
            }).catch(error => {
            console.error("Error fetching data:", error);
            setAutocompleteItems([]);
        });
    };

    return (
        <div className="main-page">
            <Header searchTerm={searchTerm} setSearchTerm={setSearchTerm} autocompleteItems={autocompleteItems} />
            <MenuBar/>
            <RankingSection/>
            <Footer/>
        </div>
    );
}

export default MainPage;
