import React, { useEffect, useState } from 'react';

const SERVER_URL = "http://localhost:8080";

function ProductList({ searchName }) {
    const [items, setItems] = useState([]);
    const [displayedItems, setDisplayItems] = useState([]);

    useEffect(() => {
        if (searchName) {
            fetch(`${SERVER_URL}/search/products?name=${searchName}`)
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    if (Array.isArray(data)) {
                        setItems(data);
                        setDisplayItems(data.slice(0, 10));
                    } else {
                        console.error("Expected an array but received:", data);
                        setItems([]);
                    }
                })
                .catch(error => {
                    console.error("Error fetching products:", error);
                });
        }
    }, [searchName]);

    return (
        <main className="product-list">
            {items.map(item => (
                <div key={item.productId} className="product-item">
                    <img src={item.productImage} alt={item.name} width="100" />
                    <h4>{item.name}</h4>
                    <p className="rating-display">
                        <span>‚≠ê</span>
                        {item.averageRating.toFixed(2)} ({item.reviewCount} reviews)
                    </p>
                </div>
            ))}
        </main>
    );
}

export default ProductList;
