import React, { useEffect, useState } from 'react';

function ProductList({ items }) {
    const [displayedItems, setDisplayItems] = useState(items.slice(0, 5));

    useEffect(() => {
        setDisplayItems(items.slice(0, 5));
    }, [items]);

    const handleLoadMore = () => {
        const nextItems = items.slice(displayedItems.length, displayedItems.length + 10);
        setDisplayItems([...displayedItems, ...nextItems]);
    };

    return (
        <main className="product-list">
            {displayedItems.map(item => (
                <div key={item.productId} className="product-item">
                    <img src={item.productImage} alt={item.productName} width="100" />
                    <h4>{item.productName}</h4>
                    <p className="rating-display">
                        <span>⭐</span>
                        {item.averageRating.toFixed(2)} ({item.reviewCount} reviews)
                    </p>
                </div>
            ))}
            {items && displayedItems.length < items.length && (
                <button onClick={handleLoadMore} className="load-more">더보기</button>
            )}
        </main>
    );
}

export default ProductList;
