import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/SearchResultPage.css';
import Header from "../components/Header";
import Filters from "../components/Filters";
import ProductList from "../components/ProductList";
import Footer from "../components/Footer";
import NavigationBar from "../components/NavigationBar";

function SearchResultPage() {
    const [items, setItems] = useState([]);
    const [displayedItems, setDisplayItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [autocompleteItems, setAutocompleteItems] = useState([]);
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const [searchName, setSearchName] = useState(searchParams.get('name') || '');
    const navigate = useNavigate();
    const SERVER_URL = "http://localhost:8080";

    const handleKkiniChange = async (kkini, isChecked) => {
        if (isChecked) {
            try {
                const response = await fetch
                (`${SERVER_URL}/search/kkini-products/${encodeURIComponent(kkini)}`);
                const data = await response.json();
                if (Array.isArray(data)) {
                    setItems(data);
                    setDisplayItems(data.slice(0, 5));
                } else {
                    console.error("Expected an array but received:", data);
                    setItems([]);
                }
            } catch (error) {
                console.error("Error fetching products by kkini type:", error);
            }
        }
    };

    const handleCategoryChange = async (category, isChecked) => {
        if (isChecked) {
            try {
                const response =
                    await fetch(`${SERVER_URL}/products/categoryChecked?categoryName=${category}`);
                const data = await response.json();
                if (Array.isArray(data)) {
                    setItems(data);
                    setDisplayItems(data.slice(0, 10));
                } else {
                    console.error("Expected an array but received:", data);
                    setItems([]);
                }
            } catch (error) {
                console.error("Error fetching products by category:", error);
            }
        }
    };

    const handleFilterChange = (filter, isChecked) => {
        // 필터 변경 로직
    };

    return (
        <div className="search-result-page">
            <Header
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                autocompleteItems={autocompleteItems}
                setAutocompleteItems={setAutocompleteItems}
            />
            <NavigationBar/>
            <div className="content-wrapper">
                <Filters
                    onCategoryChange={handleCategoryChange}
                    onFilterChange={handleFilterChange}
                    onKkiniChange={handleKkiniChange}
                />
                <ProductList items={displayedItems} />
            </div>
            <Footer />
        </div>
    );
}

export default SearchResultPage;
