import React, {useEffect} from 'react';

function Filters({ items, displayedItems, setItems, setDisplayItems, useEffect }) {
    const categories = ["간식", "육가공", "음료", "즉석섭취식품"];
    const filters = ["저칼로리", "슈가프리", "로우슈가", "저탄수화물",
        "키토", "트랜스지방", "고단백", "저나트륨", "포화지방", "저지방"];
    const kkinis = ["끼니", "끼니 그린"];
    const SERVER_URL = "http://localhost:8080";

    const handleKkiniChange = async (kkini, isChecked) => {
        if (isChecked) {
            try {
                const response = await fetch
                (`${SERVER_URL}/search/kkini-products/${encodeURIComponent(kkini)}`);
                const data = await response.json();
                console.log("Data from handleKkiniChange:", data);  // <-- 추가된 코드
                if (Array.isArray(data)) {
                    setItems(data);
                    setDisplayItems(data.slice(0, 5));
                } else {
                    console.error("Expected an array but received:", data);
                    setItems([]);
                }
            } catch (error) {
                console.error("Error fetching products by kkini type:", error);
            }
        }
    };

    const handleCategoryChange = async (category, isChecked) => {
        if (isChecked) {
            try {
                const response = await fetch(`${SERVER_URL}/products/categoryChecked?categoryName=${category}`);
                const data = await response.json();
                console.log("Data from handleCategoryChange:", data);  // <-- 추가된 코드
                if (Array.isArray(data)) {
                    setItems(data);
                    setDisplayItems(data.slice(0, 10));
                } else {
                    console.error("Expected an array but received:", data);
                    setItems([]);
                }
            } catch (error) {
                console.error("Error fetching products by category:", error);
            }
        }
    };


    const handleFilterChange = (filter, isChecked) => {
        // 필터 변경 로직
    };

    useEffect(() => {
        console.log("Updated items:", items);
        console.log("Updated displayedItems:", displayedItems);
    }, [items, displayedItems]);

    return (
        <div className="filters">
            <h3>끼니 선택</h3>
            {kkinis.map(kkini => (
                <div key={kkini} className="filter-item">
                    <label>
                        <input
                            type="checkbox"
                            value={kkini}
                            onChange={
                                (e) => handleKkiniChange(e.target.value, e.target.checked)}
                        />
                        {kkini}
                    </label>
                </div>
            ))}
            <h3>카테고리</h3>
            {categories.map(category => (
                <div key={category} className="filter-item">
                    <label>
                        <input
                            type="checkbox"
                            value={category}
                            onChange={(e) => handleCategoryChange(e.target.value, e.target.checked)}
                        />
                        {category}
                    </label>
                </div>
            ))}
            <h3>필터</h3>
            {filters.map(filter => (
                <div key={filter} className="filter-item">
                    <label>
                        <input
                            type="checkbox"
                            value={filter}
                            onChange={(e) => handleFilterChange(e.target.value, e.target.checked)}
                        />
                        {filter}
                    </label>
                </div>
            ))}
        </div>
    )};

    export default Filters;
