import React from 'react';
import { useNavigate } from 'react-router-dom';

const SERVER_URL = "http://localhost:8080";

function Filters() {
    const categories = ["간식", "육가공", "음료", "즉석섭취식품"];
    const filters = ["저칼로리", "슈가프리", "로우슈가", "저탄수화물", "키토", "트랜스지방",
        "고단백", "저나트륨", "포화지방", "저지방"];

    return (
        <div className="filters">
            <h3>끼니 선택</h3>
            <div className="kkini-selection">
                <label>
                    <input type="radio" name="kkini" value="kkini" onChange={handleKkiniChange} defaultChecked/>
                    끼니
                </label>
                <label>
                    <input type="radio" name="kkini" value="kkiniGreen" onChange={handleKkiniChange} />
                    끼니 그린
                </label>
            </div>

            <h3>카테고리</h3>
            {categories.map(category => (
                <div key={category} className="category-selection">
                    <label>
                        <input
                            type="checkbox"
                            value={category}
                            onChange={(e) => handleCategoryChange(e.target.value, e.target.checked)}
                            checked={selectedCategories.includes(category)}
                        />
                        {category}
                    </label>
                </div>
            ))}

            <h3>필터</h3>
            {filters.map(filter => (
                <div key={filter} className="filter-selection">
                    <label>
                        <input
                            type="checkbox"
                            value={filter}
                            onChange={(e) => onFilterChange(e.target.value, e.target.checked)}
                            checked={selectedFilters.includes(filter)}
                        />
                        {filter}
                    </label>
                </div>
            ))}
        </div>
    );
}

export default Filters;
