import React from 'react';

const SERVER_URL = "http://localhost:8080";

function Filters({ items, searchName, setItems, setDisplayItems, onCategoryChange, onFilterChange, selectedCategories, selectedFilters }) {
    const categories = ["간식", "육가공", "음료", "즉석섭취식품"];
    const filters = ["저칼로리", "슈가프리", "로우슈가", "저탄수화물",
        "키토", "트랜스지방", "고단백", "저나트륨", "포화지방", "저지방"];
    const kkinis = ["끼니", "끼니 그린"];

    const filteredItems = items.filter(item =>
        selectedCategories.includes(item.category) &&
        selectedFilters.every(filter => item.filters.includes(filter))
    );

    const handleKkiniChange = (e) => {
        const kkiniType = e.target.value;

        if (kkiniType === 'kkini') {
            // 끼니 상품만 필터링
            const filteredItems = items.filter(item => item.isKkini === true);
            setItems(filteredItems);
        } else if (kkiniType === 'kkiniGreen') {
            // 끼니 그린 상품만 필터링
            const filteredItems = items.filter(item => item.isKkini === false);
            setItems(filteredItems);
        } else {
            // 모든 상품 표시
            setItems(items);
        }
    };

    return (
        <div className="filters">
            <h3>끼니 선택</h3>
            <div className="kkini-selection">
                <label>
                    <input type="radio" name="kkini" value="all" onChange={handleKkiniChange} defaultChecked />
                    전체 (끼니 & 끼니 그린)
                </label>
                <label>
                    <input type="radio" name="kkini" value="kkiniGreen" onChange={handleKkiniChange} />
                    끼니 그린
                </label>
            </div>

            <h3>카테고리</h3>
            {categories.map(category => (
                <div key={category} className="filter-item">
                    <label>
                        <input
                            type="checkbox"
                            value={category}
                            onChange={(e) => onCategoryChange(e.target.value, e.target.checked)}
                            checked={selectedCategories.includes(category)}
                        />
                        {category}
                    </label>
                </div>
            ))}
            <h3>필터</h3>
            {filters.map(filter => (
                <div key={filter} className="filter-item">
                    <label>
                        <input
                            type="checkbox"
                            value={filter}
                            onChange={(e) => onFilterChange(e.target.value, e.target.checked)}
                        />
                        {filter}
                    </label>
                </div>
            ))}
        </div>
    );
}

export default Filters;
